$(document).ready(function(){$('.insta-detail').click(function(e){showInstaModal()});$('.insta-detail-close, .insta-modal-backdrop').click(function(e){closeInstaModal()})});function showInstaModal(){$('.insta-modal').fadeIn('fast');$('.insta-modal-backdrop').fadeIn('slow');var currentWinPosition=$(window).scrollTop();var newModalPosition=currentWinPosition+40;$('.insta-modal').css({"top":newModalPosition})}
function closeInstaModal(){$('.insta-modal-backdrop').fadeOut('fast');$('.insta-modal').fadeOut('slow')}